import React, { useState, useCallback } from "react";
import ChatBot from "./ChatBot";
import Chart from "./Chart";

const Dashboard: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [splitPosition, setSplitPosition] = useState(50); // Initial split at 50%

  const handleMouseDown = (e: React.MouseEvent) => {
    setIsDragging(true);
    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
  };

  const handleMouseMove = useCallback(
    (e: MouseEvent) => {
      if (isDragging) {
        const container = document.getElementById("dashboard-container");
        if (container) {
          const containerRect = container.getBoundingClientRect();
          const newPosition =
            ((e.clientX - containerRect.left) / containerRect.width) * 100;
          // Limit the resize between 20% and 80% of the total width
          const clampedPosition = Math.min(Math.max(newPosition, 20), 80);
          setSplitPosition(clampedPosition);
        }
      }
    },
    [isDragging]
  );

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
    document.removeEventListener("mousemove", handleMouseMove);
    document.removeEventListener("mouseup", handleMouseUp);
  }, [handleMouseMove]);

  return (
    <div
      id="dashboard-container"
      className="flex h-screen bg-gray-100 relative"
    >
      {/* Left panel - ChatBot */}
      <div
        className="h-full overflow-hidden"
        style={{ width: `${splitPosition}%` }}
      >
        <ChatBot />
      </div>

      {/* Resizer */}
      <div
        className="w-1 bg-gray-300 hover:bg-blue-500 cursor-col-resize relative"
        onMouseDown={handleMouseDown}
      >
        <div
          className={`absolute left-1/2 top-1/2 transform -translate-x-1/2 -translate-y-1/2 w-4 h-16 
            flex items-center justify-center bg-gray-300 rounded-md
            ${isDragging ? "bg-blue-500" : "hover:bg-blue-500"}`}
        >
          <div className="w-0.5 h-8 bg-gray-500"></div>
        </div>
      </div>

      {/* Right panel - Chart */}
      <div
        className="h-full overflow-hidden"
        style={{ width: `${100 - splitPosition}%` }}
      >
        <Chart />
      </div>

      {/* Overlay when dragging */}
      {isDragging && <div className="fixed inset-0 z-50 cursor-col-resize" />}
    </div>
  );
};

export default Dashboard;
