import React, { useState, useCallback } from "react";
import ChatBot from "./ChatBot";
import Chart from "./Chart";

const Dashboard: React.FC = () => {
  const [isDragging, setIsDragging] = useState(false);
  const [splitPosition, setSplitPosition] = useState(30); // Initial split at 30%

  const handleMouseDown = (e: React.MouseEvent) => {
    e.preventDefault();
    setIsDragging(true);
    document.addEventListener("mousemove", handleMouseMove);
    document.addEventListener("mouseup", handleMouseUp);
  };

  const handleMouseMove = useCallback(
    (e: MouseEvent) => {
      if (isDragging) {
        const container = document.getElementById("dashboard-container");
        if (container) {
          const containerRect = container.getBoundingClientRect();
          const newPosition =
            ((e.clientX - containerRect.left) / containerRect.width) * 100;
          // Limit the resize between 20% and 80% of the total width
          const clampedPosition = Math.min(Math.max(newPosition, 20), 80);
          setSplitPosition(clampedPosition);
        }
      }
    },
    [isDragging]
  );

  const handleMouseUp = useCallback(() => {
    setIsDragging(false);
    document.removeEventListener("mousemove", handleMouseMove);
    document.removeEventListener("mouseup", handleMouseUp);
  }, [handleMouseMove]);

  return (
    <div
      id="dashboard-container"
      className="flex h-screen bg-gray-100 relative select-none"
    >
      {/* Left panel - ChatBot */}
      <div className="h-full" style={{ width: `${splitPosition}%` }}>
        <ChatBot />
      </div>

      {/* Draggable Resizer */}
      <div
        className="w-1 cursor-col-resize bg-transparent hover:bg-blue-400 transition-colors duration-200 relative"
        onMouseDown={handleMouseDown}
      >
        {/* Visible line */}
        <div className="absolute inset-0 w-px bg-gray-300 hover:bg-blue-400" />

        {/* Handle indicator - only visible on hover */}
        <div
          className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 
                      w-4 h-8 opacity-0 hover:opacity-100 transition-opacity duration-200"
        >
          <div className="w-1 h-full mx-auto bg-blue-400 rounded-full" />
        </div>
      </div>

      {/* Right panel - Chart */}
      <div className="h-full" style={{ width: `${100 - splitPosition}%` }}>
        <Chart />
      </div>

      {/* Overlay when dragging */}
      {isDragging && (
        <div className="fixed inset-0 z-50 cursor-col-resize bg-transparent" />
      )}
    </div>
  );
};

export default Dashboard;
