import React, { useState } from "react";
import { PrismLight as SyntaxHighlighter } from "react-syntax-highlighter";
import sql from "react-syntax-highlighter/dist/esm/languages/prism/sql";
import { materialDark } from "react-syntax-highlighter/dist/esm/styles/prism";
import { format } from "sql-formatter";
import Editor from "react-simple-code-editor";
import { highlight, languages } from "prismjs";
import "prismjs/components/prism-sql";
import "prismjs/themes/prism.css";
import Chart from "./Chart";

interface Message {
  id: number;
  text: string;
  isUser: boolean;
  isEditable?: boolean;
}

interface ChatBotProps {
  setChartData: (data: any) => void;
  setTableData: (data: any[]) => void;
}

interface ChartData {
  labels: string[];
  datasets: {
    label: string;
    data: number[];
    borderColor: string;
    tension: number;
  }[];
}

SyntaxHighlighter.registerLanguage("sql", sql);

const ChatBot: React.FC<ChatBotProps> = ({ setChartData, setTableData }) => {
  const [messages, setMessages] = useState<Message[]>([]);
  const [input, setInput] = useState("");
  const [tableData, setTableDataState] = useState<any[]>([]);
  const [chartData, setChartDataState] = useState<ChartData | null>(null);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (!input.trim()) return;

    // Add user message
    const userMessage: Message = {
      id: Date.now(),
      text: input,
      isUser: true,
    };

    // Simulate bot response with formatted SQL
    const botMessage: Message = {
      id: Date.now() + 1,
      text: format(
        `SELECT id, name, email, created_at FROM users WHERE name = '${input}' ORDER BY created_at DESC LIMIT 10;`
      ),
      isUser: false,
      isEditable: true,
    };

    setMessages([...messages, userMessage, botMessage]);
    setInput("");
  };

  const handleEdit = (id: number, newText: string) => {
    setMessages(
      messages.map((message) =>
        message.id === id ? { ...message, text: newText } : message
      )
    );
  };

  const handleSend = (sql: string) => {
    // Simulate sending the SQL to the backend server and receiving data
    setTimeout(() => {
      const fakeTableData = [
        {
          id: 1,
          name: "John Doe",
          email: "john.doe@example.com",
          created_at: "2023-01-01T10:00:00Z",
        },
        {
          id: 2,
          name: "Jane Smith",
          email: "jane.smith@example.com",
          created_at: "2023-01-02T11:30:00Z",
        },
        {
          id: 3,
          name: "Alice Johnson",
          email: "alice.johnson@example.com",
          created_at: "2023-01-03T12:45:00Z",
        },
        {
          id: 4,
          name: "Bob Brown",
          email: "bob.brown@example.com",
          created_at: "2023-01-04T14:00:00Z",
        },
        {
          id: 5,
          name: "Charlie Davis",
          email: "charlie.davis@example.com",
          created_at: "2023-01-05T15:15:00Z",
        },
        {
          id: 6,
          name: "Diana Evans",
          email: "diana.evans@example.com",
          created_at: "2023-01-06T16:30:00Z",
        },
        {
          id: 7,
          name: "Ethan Foster",
          email: "ethan.foster@example.com",
          created_at: "2023-01-07T17:45:00Z",
        },
        {
          id: 8,
          name: "Fiona Green",
          email: "fiona.green@example.com",
          created_at: "2023-01-08T19:00:00Z",
        },
        {
          id: 9,
          name: "George Harris",
          email: "george.harris@example.com",
          created_at: "2023-01-09T20:15:00Z",
        },
        {
          id: 10,
          name: "Hannah White",
          email: "hannah.white@example.com",
          created_at: "2023-01-10T21:30:00Z",
        },
      ];

      const labels = fakeTableData.map((row) => row.name);
      const chartData = {
        labels,
        datasets: [
          {
            label: "User Data",
            data: fakeTableData.map((row) => row.id),
            borderColor: "rgb(75, 192, 192)",
            tension: 0.1,
          },
        ],
      };

      setTableDataState((prevData: any[]) => [...prevData, ...fakeTableData]);
      setTableData((prevData: any[]) => [...prevData, ...fakeTableData]);
      setChartDataState((prevData) => {
        if (!prevData) {
          return chartData;
        }
        const newLabels = [...prevData.labels, ...labels];
        const newData = [
          ...prevData.datasets[0].data,
          ...chartData.datasets[0].data,
        ];
        return {
          labels: newLabels,
          datasets: [
            {
              ...prevData.datasets[0],
              data: newData,
            },
          ],
        };
      });
    }, 1000); // Simulate network delay

    // Original handleSend function
    /*
    fetch("/api/execute-sql", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ sql }),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Success:", data);
        // Update table data
        setTableData((prevData: any[]) => [...prevData, ...data.tableData]);

        // Update chart data
        const labels = data.tableData.map((row: any) => row.name);
        const chartData = {
          labels,
          datasets: [
            {
              label: "User Data",
              data: data.tableData.map((row: any) => row.id),
              borderColor: "rgb(75, 192, 192)",
              tension: 0.1,
            },
          ],
        };
        setChartData((prevData: any) => {
          const newLabels = [...prevData.labels, ...labels];
          const newData = [...prevData.datasets[0].data, ...chartData.datasets[0].data];
          return {
            labels: newLabels,
            datasets: [
              {
                ...prevData.datasets[0],
                data: newData,
              },
            ],
          };
        });
      })
      .catch((error) => {
        console.error("Error:", error);
      });
    */
  };

  return (
    <div className="flex flex-col h-full bg-white rounded-lg shadow-lg overflow-hidden">
      {/* Title Bar */}
      <div className="bg-blue-500 text-white p-4 text-center font-bold text-lg">
        ChatBot
      </div>

      {/* Chat Messages Area */}
      <div className="flex-1 overflow-y-auto p-4 bg-gray-50">
        {messages.length === 0 ? (
          <div className="text-center text-gray-500 mt-4">
            Start a conversation...
          </div>
        ) : (
          messages.map((message) => (
            <div
              key={message.id}
              className={`mb-4 ${message.isUser ? "text-right" : "text-left"}`}
            >
              <div
                className={`inline-block max-w-[80%] p-3 rounded-lg ${
                  message.isUser
                    ? "bg-blue-500 text-white rounded-br-none"
                    : "bg-gray-200 text-gray-800 rounded-bl-none"
                }`}
              >
                {message.isUser ? (
                  message.text
                ) : message.isEditable ? (
                  <div className="relative">
                    <Editor
                      value={message.text}
                      onValueChange={(newText) =>
                        handleEdit(message.id, newText)
                      }
                      highlight={(code) =>
                        highlight(code, languages.sql, "sql")
                      }
                      padding={10}
                      style={{
                        fontFamily: '"Fira code", "Fira Mono", monospace',
                        fontSize: 12,
                        backgroundColor: "#f5f5f5",
                        borderRadius: "5px",
                        border: "1px solid #ddd",
                      }}
                    />
                    <button
                      onClick={() => handleSend(message.text)}
                      className="absolute top-0 right-0 mt-2 mr-2 px-2 py-1 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
                    >
                      Run
                    </button>
                  </div>
                ) : (
                  <SyntaxHighlighter
                    language="sql"
                    style={materialDark}
                    className="rounded-md"
                  >
                    {message.text}
                  </SyntaxHighlighter>
                )}
              </div>
            </div>
          ))
        )}
      </div>

      {/* Chart and Table Area */}
      <div className="border-t border-gray-200 p-4 bg-white">
        {chartData && (
          <div className="mb-4">
            <Chart data={chartData} tableData={tableData} />
          </div>
        )}
      </div>

      {/* Input Area */}
      <div className="border-t border-gray-200 p-4 bg-white">
        <form onSubmit={handleSubmit} className="flex gap-2">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            className="flex-1 p-2 border border-gray-300 rounded-lg focus:outline-none focus:border-blue-500 focus:ring-1 focus:ring-blue-500"
            placeholder="Type your message..."
          />
          <button
            type="submit"
            className="px-4 py-2 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2"
          >
            Send
          </button>
        </form>
      </div>
    </div>
  );
};

export default ChatBot;
